                    if (options.width) {
                    	if (bubbleType == 'border-unit-helper') {
                    		left = element_left - options.width - 40;
                    		top = top + 20;
                    	}
                    	bubble.find('.bubble_popup_arrow').remove();
                    	bubble.find('.bubble_popup_arrow2').remove();
                    }
